mapper.select:
select
  id, pwd, user_name, sex, head_pic, birthday, major, school_date, education, email, phone, state, type, activation_code, create_time, salt, nick_name, province, city        from t_user
where id = #{id}

select
    id, pwd, user_name, sex, head_pic, birthday, major, school_date, education, email, phone, state, type, activation_code, create_time, salt, nick_name, province, city        from t_user
where user_name = #{userName}

select
    id, pwd, user_name, sex, head_pic, birthday, major, school_date, education, email, phone, state, type, activation_code, create_time, salt, nick_name, province, city        from t_user
where email = #{email}

select
    id, pwd, user_name, sex, head_pic, birthday, major, school_date, education, email, phone, state, type, activation_code, create_time, salt, nick_name, province, city        from t_user
limit #{offset},#{rows}
